<?php/*** TestGuest Version1.0* ================================================* Copy 2017-4-9* Web: http://www.yc60.com* ================================================* Author: Lee* Date: 2017-4-9*/session_start();// 定义常量，用来指定本页内容define('SCRIPT','register');//设置字符编码header('Content-Type:text/html;charset=utf-8');// 定义常量，用来授权调用includes里面的文件define('IN_TG',true);//引入公共文件require dirname(__FILE__).'/includes/common.inc.php';//判断是否提交了if(isset($_GET['action'])=='register'){    //防止恶意注册,!我测试的    _check_code(isset($_POST['code']),isset($_SESSION['code']));    // 引入验证文件    include ROOT_PATH.'includes/register.func.php';    //创建一个空数组，用来存放提交过来的合法数据    $_clean=array();    $_clean['uniqid']=_check_uniqid($_POST['uniqid'],$_SESSION['uniqid']);      //唯一标识符    $_clean['active']=_sha1_uniqid();                                           //激活处理    $_clean['username']=_check_username($_POST['username'],2,20);    $_clean['password']=_check_password($_POST['password'],$_POST['notpassword'],6);    $_clean['question']=_check_question($_POST['question'],4,20);    $_clean['answer']=_check_answer($_POST['question'],$_POST['answer'],4,20);    $_clean['sex']=_check_sex($_POST['sex']);    $_clean['face']=_check_face($_POST['face']);    $_clean['email']=_check_email($_POST['email'],6,40);    $_clean['qq']=_check_qq($_POST['qq']);    $_clean['url']=_check_url($_POST['url'],40);    //在新增之前，判断用户名是否重复    _is_repeat(        "SELECT tg_username FROM tg_user WHERE tg_username='{$_clean['username']}' LIMIT 1",        '用户名已被注册'    );    //新增用户    _query(<<<TAG    INSERT INTO tg_user(                                      tg_uniqid,                                      tg_active,                                      tg_username,                                      tg_password,                                      tg_question,                                      tg_answer,                                      tg_sex,                                      tg_face,                                      tg_email,                                      tg_qq,                                      tg_url,                                      tg_reg_time,                                      tg_last_time,                                      tg_last_ip                                      ) VALUES (                                      '{$_clean['uniqid']}',                                      '{$_clean['active']}',                                      '{$_clean['username']}',                                      '{$_clean['password']}',                                      '{$_clean['question']}',                                      '{$_clean['answer']}',                                      '{$_clean['sex']}',                                      '{$_clean['face']}',                                      '{$_clean['email']}',                                      '{$_clean['qq']}',                                      '{$_clean['url']}',                                      NOW(),                                      NOW(),                                      '{$_SERVER["REMOTE_ADDR"]} '                                      )TAG);    if(_affected_rows()==1){        //关闭数据库        _close();        //跳转        _session_destroy();        _location('注册成功！','active.php?active='.$_clean['active']);    }else{        //关闭数据库        _close();        //跳转        _session_destroy();        _location('注册失败！','register.php');    }}$_SESSION['uniqid']=$_uniqid=_sha1_uniqid();?><!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <title>多用户留言系统--注册</title><?php    require ROOT_PATH.'includes/title.inc.php';?>    <script type="text/javascript" src="js/code.js"></script><script type="text/javascript" src="js/register.js"></script></head><body><?php    require ROOT_PATH.'includes/header.inc.php';?><div id="register">    <h2>会员注册</h2>    <form action="register.php?action=register" name="register" method="post">        <input type="hidden" name="uniqid" value="<?php echo $_uniqid ?>">        <dl>            <dt>请认真填写以下内容</dt>            <dd>用&nbsp;&nbsp;户&nbsp;&nbsp;名：<input type="text" name="username" class="text"> (*必填，至少两位)</dd>            <dd>密&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;码：<input type="password" name="password" class="text"> (*必填，至少六位)</dd>            <dd>确认密码：<input type="password" name="notpassword" class="text"> (*同上)</dd>            <dd>密码提示：<input type="text" name="question" class="text"> (*必填，至少两位)</dd>            <dd>密码回答：<input type="text" name="answer" class="text"> (*必填，至少两位)</dd>            <dd>性&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;别：<input type="radio" name="sex" value="男" checked="checked">男<input type="radio" name="sex" value="女">女</dd>            <dd class="face"><input type="hidden" name="face" value="face/m01.gif"><img src="face/m01.gif" alt="头像选择" id="faceimg"></dd>            <dd>电子邮件：<input type="text" name="email" class="text"> (*必填，激活账户)</dd>            <dd>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Q &nbsp;&nbsp;Q：<input type="text" name="qq" class="text"></dd>            <dd>主页地址：<input type="text" name="url" class="text" value="http://"></dd>            <dd>验&nbsp;&nbsp;证&nbsp;&nbsp;码：<input type="text" name="code" class="text code"><img src="code.php" id="code" /></dd>            <dd><input type="submit" class="submit" value="注册"></dd>        </dl>    </form></div><?php    require ROOT_PATH.'includes/footer.inc.php';?></body></html>